package JavaTest1;

public class P4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i=10;
		System.out.println(i+":is before postincrement");
		System.out.println(i++);
		System.out.println(i+":is before preincrement");

		System.out.println(++i);
		System.out.println(i+":is before postdcrement");
		System.out.println(i--);
		System.out.println(i+":is before predcrement");
		System.out.println(--i);
		

	}

}
